import React, { useState } from "react";
import PropTypes from "prop-types";

const TodoItem = ({ todo }) => {
  getStyle = () => {
    return {
      background: "#f4f4f4",
      padding: "10px",
      borderBottom: "1px #ccc dotted",
      textDecoration: props.todo.completed ? "line-through" : "none",
    };
  };

